import { Component, OnInit } from '@angular/core';
import { PanierService } from 'src/app/services/panier.service';

interface PanierItem {
  id: string;
  name: string;
  price: number;
  description: string;
  imageUrl: string;
  quantite: number;
}


@Component({
  selector: 'app-panier',
  templateUrl: './panier.component.html',
  styleUrls: ['./panier.component.scss']
})
export class PanierComponent implements OnInit {
  panierItems: PanierItem[] = [];

  constructor(private panierService: PanierService) { }

  ngOnInit() {
    this.getPanierItems();
  }

  private getPanierItems() {
    this.panierService.getProduitsPanier().subscribe(items => {
      this.panierItems = items;
    });
  }

  supprimerDuPanier(id: string) {
    this.panierService.supprimerDuPanier(id.toString()).subscribe(() => {
      this.getPanierItems();
      alert('Produit supprimé du panier avec succès');
    });
  }
  
  commander() {
    // Logique de la commande
    alert('Commande passée avec succès');
  }
}
<div class="panier-container">
  <h2 class="panier-title">Panier</h2>
  <div *ngFor="let item of panierItems" class="panier-item">
    <div class="panier-image">
      <img [src]="item.imageUrl" alt="Image du produit">
    </div>
    <div class="panier-details">
      <h3 class="produit-name">{{ item.name }}</h3>
      <p class="produit-description">{{ item.description }}</p>
      <p class="produit-price">{{ item.price }}€</p>
      <div class="quantite-section">
        <label for="quantite">Quantité : {{item.quantite}}</label>
      </div>
    </div>
    <div class="panier-subtotal">
      <p class="subtotal-label">Sous-total :</p>
      <p class="subtotal-value">{{ item.price * item.quantite }}€</p>
    </div>  
    <button class="supprimer-button" (click)="supprimerDuPanier(item.id)">Supprimer</button>
  </div>
  <button class="commander-button" (click)="commander()">Passer la commande</button>
</div>
