import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { ProduitService } from 'src/app/services/produit.service';
import { Produit } from 'src/app/models/produit.model';
import { PanierService } from 'src/app/services/panier.service';

@Component({
  selector: 'app-produit-detail',
  templateUrl: './produit-detail.component.html',
  styleUrls: ['./produit-detail.component.scss']
})
export class ProduitDetailComponent implements OnInit {
  id!: number;
  produit: Produit | undefined;
  produits: Produit[] = [];
  selectedQuantity: number = 1;

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private produitService: ProduitService,
    private panierService: PanierService
  ) {}

  ngOnInit() {
    // on récupère l'id du produit
    this.id = +this.route.snapshot.params['iden'];

    this.produitService.getProducts().subscribe(
      (data: Produit[]) => {
        this.produits = data;
        this.produit = this.produits.find((item) => item.id === this.id);
      },
      (error) => {
        console.error('Error fetching products:', error);
      }
    );
  }

  redirectToPage(pageName: string) {
    this.router.navigate([`${pageName}`]);
  }

  ajouterAuPanier() {
    if (this.produit) {
      this.panierService.ajouterAuPanier(this.produit, this.selectedQuantity).subscribe(
        () => {
          alert('Produit ajouté au panier avec succès');
        },
        (error) => {
          console.error('Erreur lors de l\'ajout au panier :', error);
        }
      );
    }
  }
  

  changePrixQuantite(): number {
    if (this.produit) {
      let prix = this.produit.price * this.selectedQuantity;
      return parseFloat(prix.toFixed(2));
    }
    return 0;
  }
}
<div class="page-container">
  <div *ngIf="produit" class="product-details">
    <h2 class="product-name">{{ produit.name }}</h2>
    <div class="details-container">
      <img class="presentation-image small-image" [src]="produit.imageUrl">
      <div class="side-panel">
        <h3 class="product-price"><span id="prix1article">Prix d'un article : </span>{{ produit.price }}€</h3>
        <div class="quantity-section">
          <label for="quantity">Quantité :</label>
          <input type="number" id="quantity" min="1" [(ngModel)]="selectedQuantity">
        </div>
        <h3 class="product-price"><span id="prix1article">Prix de la sélection : </span>{{ changePrixQuantite() }}€</h3>
        <button class="add-to-cart-button" (click)="ajouterAuPanier()">Ajouter au panier</button>
        <button class="buy-now-button">Acheter ce produit</button>
      </div>
    </div>
    <p class="product-description">{{ produit.description }}</p>
    <button class="presentation-button" (click)="redirectToPage('all-produit')">Retour</button>
  </div>
</div>
